import React from 'react';
import Chart from './Chart';

export default class Graph extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        temp: [],
        humidity: [],
        pressure: [],
        name: '',
        error: false
      }
    }

    renderGraph = () => {
        const { temp, pressure, humidity } = this.state;
        return (
          <tr key={temp}>
            <td><Chart data={temp} color="orange" units="K" /></td>
            <td><Chart data={pressure} color="blue" units="hPa" /></td>
            <td><Chart data={humidity} color="green" units="%" /></td>
          </tr>
        );
      }

      render() {
        const { temp, pressure, humidity, name } = this.state;
        return (
          <>
            {this.state.error ||
              <div>
                <WeatherList temp={temp[0]} pressure={pressure[0]} humidity={humidity[0]} />
                <TempDisplay data={temp} name={name} />
                <table className="table table-hover">
                  <thead>
                    <tr>
                      <th> Temperature(K) </th>
                      <th> Pressure(hPa) </th>
                      <th> Humidity(%) </th>
                    </tr>
                  </thead>
                  <tbody>{this.renderGraph()}</tbody>
                </table>
              </div>
            }
          </>
        );
      }

}
