import React, { Component } from "react";

import Weather from "./Weather";
import Forecast from "./Forecast";
import Chart from "./Chart";

import "../styles/WeatherAndForecast.css";


class WeatherAndForecast {
  constructor({ weatherInfo, location }) {
    const date = dateBuilder(new Date());

    function dateBuilder(d) {
      const days = [
        "Sunday",
        "Monday",
        "Tuesday",
        "Wednesday",
        "Thursday",
        "Friday",
        "Saturday"
      ];

      const date = [];
      for (let count = 0; count < 5; count++) {
        if (d.getDay() + count < 7)
          date[count] = d.getDay() + count;
        else if (d.getDay() + count === 7)
          date[count] = 0;
        else if (d.getDay() + count === 8)
          date[count] = 1;
        else if (d.getDay() + count === 9)
          date[count] = 2;
        else if (d.getDay() + count === 10)
          date[count] = 3;
      }

      return [
        days[date[0]],
        days[date[1]],
        days[date[2]],
        days[date[3]],
        days[date[4]]
      ];
    }

    this.state = {
      temp: [],
      humidity: [],
      pressure: [],
      name: '',
      error: false
    };

    function graphBuilder() {
      const { temp, pressure, humidity } = this.state;
      return (
        <tr key={temp}>
          <td><Chart data={temp} color="orange" units="K" /></td>
          <td><Chart data={pressure} color="blue" units="hPa" /></td>
          <td><Chart data={humidity} color="green" units="%" /></td>
        </tr>
      );
    }


    return (
      <div className="WeatherAndForecast">
        <Weather
          weatherInfo={weatherInfo}
          location={location}
          date={date[0]} />
        <div className="WeatherAndForecast__container">
          <Forecast
            weatherInfo={weatherInfo.daily[0]}
            date={date[0]} />
          <Forecast
            weatherInfo={weatherInfo.daily[1]}
            date={date[1]} />
          <Forecast
            weatherInfo={weatherInfo.daily[2]}
            date={date[2]} />
          <Forecast
            weatherInfo={weatherInfo.daily[3]}
            date={date[3]} />
          <Forecast
            weatherInfo={weatherInfo.daily[4]}
            date={date[4]} />
        </div>
        <div>
          <table>
            <thead>
              <tr>
                <th> Temperature(K) </th>
                <th> Pressure(hPa) </th>
                <th> Humidity(%) </th>
              </tr>
            </thead>
            <tbody>{this.graphBuilder()}</tbody>
          </table>
        </div>
      </div>
    );
  }
}


export default WeatherAndForecast;
