import React, {useState, useEffect} from "react";
// import CardList from "./CardList";
import SearchBox from './Components/SearchBox';
import Loading from "./Components/Loading";
import NoResults from "./Components/NoResults";

function App() {

// ========= setting the initial states for items and search ===========
    const [items, setItems] = useState([]);
    const [search, setSearch] = useState("");

// ============ fetching the data from API and store values ============
    useEffect(() => {
        fetchItems()
    }, []);


    const fetchItems = async() => {
        const data = await fetch('https://api.openweathermap.org/data/2.5/onecall?lat=55&lon=61&exclude=minutely,hourly,alerts&appid=24cae5857cc62be2254828080a25b0b6');
        const items = await data.json();


        //const items = itemsObj.hits.hits;
        console.log(items);

// ============ store just fetched items in an array ============
        setItems(items);
    }


// ============ rendering the App ============ 
    let noItems = !setItems;

    return noItems ? <Loading/> : (

            <div className="App">
                <h1 className="heading">W</h1>
                <div className="app-body">

                    {<SearchBox searchChange={(event) => setSearch(event.target.value)}/> 

                    /*{noPlays ? <NoResults /> : <CardList plays={currentPosts}/> } */}


                </div>
            </div>
        );
}

export default App;





